import * as Ast from "./ast.mjs";
import * as Walk from "./walk.mjs";
import * as JS2Scheme from "./_js2scheme.hop";
import * as List from "./_list.hop";

export { generate };

/*---------------------------------------------------------------------*/
/*    Fragment ...                                                     */
/*---------------------------------------------------------------------*/
class Fragment {
   #js;
   
   constructor(js) {
      this.#js = js;
   }
   
   toString() {
      return this.#js;
   }

   mappings(source, target) {
      return [];
   }
}

/*---------------------------------------------------------------------*/
/*    generate ...                                                     */
/*---------------------------------------------------------------------*/
function generate(ast, conf) {
   return new Fragment(JS2Scheme.generate(ast, conf));
}
   
