/*---------------------------------------------------------------------*/
/*    Config ...                                                       */
/*---------------------------------------------------------------------*/
export function Config(obj) {
   return obj;
}

/*---------------------------------------------------------------------*/
/*    configGet ...                                                    */
/*---------------------------------------------------------------------*/
export function configGet(conf, opt) {
   const k = typeof opt === "string" ? opt : Object.keys(opt)[0];
   if (k in conf) {
      return conf[k];
   } else {
      if (typeof opt === "string") {
	 return undefined;
      } else {
	 return opt[k];
      }
   }
}

/*---------------------------------------------------------------------*/
/*    configAdd ...                                                    */
/*---------------------------------------------------------------------*/
export function configAdd(conf, opt) {
   const k = Object.keys(opt)[0];
   conf[k] = opt[k];
   return conf;
}

/*---------------------------------------------------------------------*/
/*    confMaxInt ...                                                   */
/*---------------------------------------------------------------------*/
export function confMaxInt(conf) {
   return configGet(conf, "int-size", 1 << 52);
}

/*---------------------------------------------------------------------*/
/*    confMinInt ...                                                   */
/*---------------------------------------------------------------------*/
export function confMinInt(conf) {
   return configGet(conf, "int-size", -1 << 53);
}
