/*---------------------------------------------------------------------*/
/*    Imports                                                          */
/*---------------------------------------------------------------------*/
import { readSync, fstatSync } from "node:fs";
export { readLines, readLinesGen };

/*---------------------------------------------------------------------*/
/*    readLinesSync ...                                                */
/*---------------------------------------------------------------------*/
function readLinesSync(fd) {
   let size = fstatSync(fd).size;

   if (size === 0) {
      return [];
   } else {
      let pos = 0;
      let buffer = Buffer.allocUnsafe(size); // single buffer with file data
      let done = false;
      let bytesRead = 0;
      
      while (!done) {
	 let threw = true;

         bytesRead = readSync(fd, buffer, pos, size - pos);
	 
	 pos += bytesRead;
	 done = (bytesRead === 0) || (pos >= size);
      }

      if (pos < size) {
	 buffer = buffer.slice(0, pos);
      }
      
      const str = buffer.toString();
      const res = str.split("\n");
      if (res[res.length -1] === "\n") {
	 res.pop();
      }

      return res;
   }
}

/*---------------------------------------------------------------------*/
/*    readLines ...                                                    */
/*---------------------------------------------------------------------*/
function readLines(fd, cb) {
   if (cb) {
      //return readLinesAsync(fd, db);
   } else {
      return readLinesSync(fd);
   }
}

/*---------------------------------------------------------------------*/
/*    readLinesGen ...                                                 */
/*---------------------------------------------------------------------*/
function* readLinesGen(fd) {
   let size = fstatSync(fd).size;

   if (size === 0) {
      return false;
   } else {
      let pos = 0;
      let buffer = new Buffer(size); // single buffer with file data
      let strbuffer = "";
      let done = false;
      let bytesRead = 0;
      
      while (true) {
	 let threw = true;

         bytesRead = readSync(fd, buffer, pos, size - pos);

	 pos += bytesRead;
	 done = (bytesRead === 0) || (pos >= size);

	 const b = (bytesRead < (size - pos))
	    ? buffer.slice(0, size -pos).toString()
	    : buffer.toString();
	    
	 strbuffer += b;

	 if (strbuffer.indexOf("\n") >=0 ) {
	    const b = strbuffer.split("\n");
	    for (let i = 0; i < b.length - 1; i++) {
	       yield b[i];
	    }
	    strbuffer = b[b.length - 1];
	 }

	 if (done) {
	    return true;
	 }
      }
   }
}

